public void CreateProject (string token)
{
  string URL = baseURL + "/projects";
  string formDataBoundary = String.Format("----WebKitFormBoundary{0:N}", Guid.NewGuid());
  ProjectParams projectParams = new ProjectParams
  {
    customerId = 0,
    name = "ProjectName",
    templateId = 0
  };

  var dict = ToDictionary<object>(projectParams);
  var parameters = Encoding.ASCII.GetBytes(GetFormDataString(dict, formDataBoundary));

  try
  {
    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(URL);
    request.Method = "POST";                
    request.Headers.Add("Authorization", "XTM-Basic " + token);

    request.ContentType = "multipart/form-data; boundary=" + formDataBoundary;

    request.ContentLength = parameters.Length;
    Stream newStream = request.GetRequestStream();
    newStream.Write(parameters, 0, parameters.Length);
    newStream.Close();
    HttpWebResponse response = request.GetResponse() as HttpWebResponse;
  }
  catch (Exception ex)
  {
    Console.WriteLine(ex.Message);
  }
}
public class ProjectParams
{
  public int customerId { get; set; }
  public string name { get; set; }
  public int templateId { get; set; }
}        
public static Dictionary<string, TValue> ToDictionary<TValue>(object obj)
{
  var json = JsonConvert.SerializeObject(obj);
  var dictionary = JsonConvert.DeserializeObject<Dictionary<string, TValue>>(json);
  return dictionary;
}
public string GetFormDataString(Dictionary<string, object> parameters, string boundary)
{
  string formDataStream = "";
  bool needsRN = false;
  foreach (var param in parameters)
  {
    if (needsRN)
      formDataStream += "\r\n";

    needsRN = true;

    string header = string.Format("--{0}\r\nContent-Disposition: form-data; name=\"{1}\"\r\n\r\n{2}",
    boundary, param.Key, param.Value);

    formDataStream += header;
  }
  string footer = "\r\n--" + boundary + "--";
  formDataStream += footer;

  return formDataStream;
}

