static string baseURL = "https://SERVER_URL/rest-api";

public string Authentication ()
{            
  string URL = baseURL + "/auth/token";
  AuthorizationRequest userData = new AuthorizationRequest
  {
    client ="CLIENT",
    password = "password",
    userId = 0
  };

  var data = Encoding.ASCII.GetBytes(JsonConvert.SerializeObject(userData));

  try
  {
    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(URL);
    request.Method = "POST";
    request.ContentType = "application/json";
    request.ContentLength = data.Length;

    Stream newStream = request.GetRequestStream();
    newStream.Write(data, 0, data.Length);
    newStream.Close();

    HttpWebResponse response = request.GetResponse() as HttpWebResponse;
    StreamReader reader = new StreamReader(response.GetResponseStream());

    string authJSONResponse = reader.ReadToEnd();
    AuthorizationResponse authResponse = JsonConvert.DeserializeObject<AuthorizationResponse>(authJSONResponse);
    reader.Close();

    return authResponse.token;
  }
  catch (Exception ex)
  {
    Console.WriteLine(ex.Message);
    return null;
  }
}

public class AuthorizationRequest
{
  public string client { get; set; }
  public string password { get; set; }
  public int userId { get; set; }
}

public class AuthorizationResponse
{
  public string token { get; set; }
}

